/* 
Gutter is built with 30 pixels
[0-7]   = Window 1 FS
[8-9]   = Blank (spacer)
[10-19] = Status
[20-21] = Blank (spacer)
[22-29] = Window 2 FS
*/ 

//--- Basic variables
var input = msg.payload;
var theBldg = msg.payload.bldg;
var theWin = msg.payload.window;
var theFS = msg.payload.numFire;

//--- Get current gutterpixels, delete the relevant window's substring 
//string.substring(indexStart, indexEnd)
var curPixelString = global.get(theBldg + ".GutterPixels");
if (theWin == 1) {
    var stringMinusWindow = curPixelString.substring(7, 30); //will want to check off-by-one issues
} else if (theWin == 2) {
    var stringMinusWindow = curPixelString.substring(0, 22); //will want to check off-by-one issues
}

//--- Build the new window's string
var newWindowString = "";
//-- Add 1's up to the FS, then 0's after that to only light up the FS pixels
// off-by-one _should_ work out alright? 
// also js shorthand woo https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/Conditional_Operator
for (let i=0; i<8; i++) {
    newWindowString += i<theFS ? "1" : "0"; 
}

//--- Combino preparo 
//node.warn(newWindowString);
//node.warn(stringMinusWindow);
if (theWin == 1) {
    var newPixelString = newWindowString + stringMinusWindow; 
} else if (theWin == 2) {
    var newPixelString = stringMinusWindow + newWindowString;
}

//--- Fin
global.set(theBldg + ".GutterPixels", newPixelString);
return msg;

